# Tao bang du lieu
CREATE TABLE IF NOT EXISTS map(
    id tinyint(3) NOT NULL AUTO_INCREMENT,
    name varchar(30) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
    file varchar(100) NOT NULL,
    bg smallint(5) NOT NULL DEFAULT 0,
    mapAddY smallint(5) NOT NULL DEFAULT 0,
    bullEffShower smallint(5) NOT NULL DEFAULT 0,
    inWaterAddY smallint(5) NOT NULL DEFAULT 0,
    cl2AddY smallint(5) NOT NULL DEFAULT 0,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin;

CREATE TABLE IF NOT EXISTS item(
    item_id tinyint(3) NOT NULL AUTO_INCREMENT,
    name varchar(15) NOT NULL,
    xu smallint(4) NOT NULL,
    luong smallint(3) NOT NULL,
    nDcMang smallint(3) NOT NULL,
    PRIMARY KEY(item_id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin;

CREATE TABLE IF NOT EXISTS nhanvat(
    nhanvat_id tinyint(3) NOT NULL AUTO_INCREMENT,
    name  varchar(15) NOT NULL,
    xu    int(10) NOT NULL,
    luong smallint(3) NOT NULL,
    ma_sat_gio smallint(4) NOT NULL,
    goc_min smallint(4) NOT NULL,
    sat_thuong smallint(4) NOT NULL,
    sat_thuong_dan smallint(4) NOT NULL,
    so_dan smallint(4) NOT NULL,
    PRIMARY KEY(nhanvat_id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin;

CREATE TABLE IF NOT EXISTS captionLV(
    id tinyint(3) NOT NULL AUTO_INCREMENT,
    lvl smallint(4) NOT NULL,
    caption varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin COMMENT =  'Caption level';

CREATE TABLE IF NOT EXISTS equip(
    id smallint(5) NOT NULL AUTO_INCREMENT,
    nv tinyint(2) NOT NULL,
    equipType tinyint(2) NOT NULL,
    equipId tinyint(3) NOT NULL,
    name varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
    giaXu int(10) NOT NULL,
    giaLuong smallint(5) NOT NULL DEFAULT -1,
    giaBanXu int(10) NOT NULL DEFAULT -1,
    isSet tinyint(1) NOT NULL DEFAULT 0,
    hanSD smallint(5) NOT NULL DEFAULT 0,
    lvRequire smallint(3) NOT NULL DEFAULT 0,
    frame int(5) NOT NULL DEFAULT 0,
    bullId smallint(3) NOT NULL DEFAULT 0,
    bigCutX varchar(50) NOT NULL DEFAULT '',
    bigCutY varchar(50) NOT NULL DEFAULT '',
    bigSizeX varchar(50) NOT NULL DEFAULT '',
    bigSizeY varchar(50) NOT NULL DEFAULT '',
    bigAlignX varchar(50) NOT NULL DEFAULT '',
    bigAlignY varchar(50) NOT NULL DEFAULT '',
    addPN varchar(50) NOT NULL DEFAULT '',
    addPN100 varchar(50) NOT NULL DEFAULT '',
    onSale tinyint(1) NOT NULL DEFAULT 1,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin COMMENT =  'Trang Bi';

CREATE TABLE IF NOT EXISTS specialItem(
    id smallint(5) NOT NULL,
    name varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
    detail varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '',
    ability varchar(50) NOT NULL DEFAULT '[0,0,0,0,0]',
    giaXu int(10) NOT NULL,
    giaLuong smallint(5) NOT NULL DEFAULT -1,
    giaBanXu int(10) NOT NULL DEFAULT -1,
    hanSD smallint(5) NOT NULL DEFAULT 0,
    showChon tinyint(1) NOT NULL DEFAULT 1,
    onSale tinyint(1) NOT NULL DEFAULT 1,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin COMMENT =  'Do Dac Biet';

# Fomular
CREATE TABLE IF NOT EXISTS fomular(
    id tinyint(3) NOT NULL AUTO_INCREMENT,
    idMaterial tinyint(3) NOT NULL,
    lv tinyint(2) NOT NULL,
    name varchar(100) NOT NULL,
    lvRequire smallint(3) NOT NULL DEFAULT 0,
    equipType tinyint(2) NOT NULL,
    equipId varchar(500) NOT NULL,
    equipNeed varchar(500) NOT NULL,
    addPNMin varchar(1000) NOT NULL,
    addPNMax varchar(1000) NOT NULL,
    addPP100Min varchar(1000) NOT NULL,
    addPP100Max varchar(1000) NOT NULL,
    itemRequire varchar(2000) NOT NULL,
    detail varchar(2000) NOT NULL,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin COMMENT =  'Fomular';

# Nap tien
CREATE TABLE IF NOT EXISTS napThe(
    iddb tinyint(3) NOT NULL AUTO_INCREMENT,
    id varchar(100) NOT NULL,
    info varchar(500) NOT NULL,
    url varchar(500) NOT NULL,
    mssContent varchar(500),
    mssTo varchar(500),
    PRIMARY KEY(iddb)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin COMMENT = 'Nap tien';

# User
CREATE TABLE IF NOT EXISTS user(
    `user_id` int(15) NOT NULL AUTO_INCREMENT,
    `user` varchar(15) NOT NULL,
    `password` varchar(30) NOT NULL,
    `lock` tinyint(1) NOT NULL DEFAULT 0,
    PRIMARY KEY(user_id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin;

