# Mission require
CREATE TABLE IF NOT EXISTS missionRequire(
    id int(15) NOT NULL AUTO_INCREMENT,
    name varchar(100) NOT NULL,
    PRIMARY KEY(id)
) ENGINE='innodb' CHARACTER SET utf8 COLLATE utf8_bin;

# Mission
CREATE TABLE IF NOT EXISTS mission(
    `iddb` int(15) NOT NULL AUTO_INCREMENT,
    `id` int(15) NOT NULL,
    `level` tinyint(3) NOT NULL DEFAULT 1,
    `name` varchar(100) NOT NULL,
    `idneed` tinyint(3) NOT NULL,
    `require` int(10) NOT NULL,
    `reward` varchar(100) NOT NULL,
    `rewardXu` int(15) NOT NULL DEFAULT 0,
    `rewardLuong` int(15) NOT NULL DEFAULT 0,
